	public int[] sortArrayByParity1(int[] a) {

		//Arrays.sort(a);
		int start=0;
		int end =a.length-1;
		while(start<end) {
			if(a[start]==0) {
				start++;
			}
			else if(a[end]==0) {
				int temp=a[end];
				a[end--]=a[start];
				a[start++]=temp;
			}
			else if(a[start]%2!=0 &&a[end]%2==0) {			
				int temp=a[end];
				a[end--]=a[start];
				a[start++]=temp;	
			}
			else if(a[start]%2!=0&&a[end]%2!=0) {
				end--;
			}
			else {
				start++;
			}
		}
		return a;
	}
